```java
public  class  StringTest  {
    // 编译期已经创建好的（直接使用双引号声明的）就存储在常量池中
    public  static  String  strStatic  =  "ABC";
    public  String  strNormal  =  "ABC";
    public  String  strRuntime;

    public  void  checkValue() {

        // 运行期（new 出来的）存储在堆中
        String  strMethodNormal  =  new String("ABC");
        String  strMethodNormal2  =  new String("ABC");
        strRuntime = new String("ABC");

        System.out.println("strStatic===value====" + strStatic);
        System.out.println("strNormal===value====" + strNormal);
        System.out.println("strRuntime===value====" + strRuntime);
        System.out.println("strMethodNormal===value====" + strMethodNormal);
        System.out.println("strMethodNormal2===value====" + strMethodNormal2);

        System.out.println("---------------------------------------------------");

        System.out.println("strStatic===address====" + System.identityHashCode(strStatic));
        System.out.println("strNormal===address====" + System.identityHashCode(strNormal));
        System.out.println("strRuntime===address====" + System.identityHashCode(strRuntime));
        System.out.println("strMethodNormal===address====" + System.identityHashCode(strMethodNormal));
        System.out.println("strMethodNormal2===address====" + System.identityHashCode(strMethodNormal2));

        System.out.println("---------------------------------------------------");

        System.out.println("strStatic.equals(strNormal)====" + strStatic.equals(strNormal));
        System.out.println("strStatic.equals(strRuntime)====" + strStatic.equals(strRuntime));
        System.out.println("strRuntime.equals(strMethodNormal)====" + strRuntime.equals(strMethodNormal));
        System.out.println("strStatic.equals(strMethodNormal)====" + strStatic.equals(strMethodNormal));
        System.out.println("strStatic.equals(strMethodNormal2)====" + strStatic.equals(strMethodNormal2));

    }

    public  static  void  main(String[]  args) {
      StringTest  stringTest  =  new StringTest();
      stringTest.checkValue();
    }

}
